package com.pract1;

public class Array2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int [] s=new int[5];
		s[0]=1;
		s[1]=2;
		s[2]=5;
		s[3]=7;
		s[4]=9;
	    int [] s1=new int[] {1,2,3,4,5};
	    for(int i=0;i<s.length;i++)
	    {
	    	System.out.println("element at index"+i+":"+s[i]);
	    }
	    System.out.println("==============================");
	    for(int i=0;i<s1.length;i++)
	    {
	    	System.out.println("element at index"+i+":"+s1[i]);
	    }
	    

	}

}
